import "./App.scss";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBBtn,
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBTextArea,
} from "mdb-react-ui-kit";
import React, { useState } from "react";

function App() {
  const [inputText, setInputText] = useState("");
  const [detectedLanguage, setDetectedLanguage] = useState("");

  const handleDetectLanguage = async () => {
    try {
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: {
          'Content-Type': 'text/plain',
        },
        // body: JSON.stringify({ features: inputText }),
        body: inputText, 
      });
console.log(inputText);
      if (!response.ok) {
        throw new Error("Failed to detect language");
      }

      const data = await response.json();
      setDetectedLanguage(data.prediction);
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  return (
    <div className="App">
      <MDBNavbar expand="lg" light style={{ backgroundColor: "#e3f2fd" }}>
        <MDBContainer fluid>
          <MDBNavbarBrand href="#" style={{ fontWeight: "bold" }}>
            <img src={require("./icon.png")} height="35" width="35" alt="" />
            Language Identifier
          </MDBNavbarBrand>
        </MDBContainer>
      </MDBNavbar>
      <br />
      <div className="d-flex">
        <div className="p-2 flex-fill" style={{ width: "70vw" }}>
          <MDBCard>
            <MDBCardBody>
              <MDBCardTitle className="text-start" style={{ color: "blue" }}>
                Input Text
              </MDBCardTitle>
              <MDBTextArea
                label="Input your Text"
                id="textAreaExample"
                rows={4}
                style={{ height: "60vh" }}
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
              />
              <br />
              <MDBBtn onClick={handleDetectLanguage}>Detect Language</MDBBtn>
            </MDBCardBody>
          </MDBCard>
        </div>
        <div className="p-2 flex-fill">
          <MDBCard
            style={{
              backgroundColor: "transparent",
              boxShadow: "none",
              marginRight: "1vw",
            }}
          >
            <MDBCardBody>
              <MDBCardTitle className="text-start" style={{ color: "blue" }}>
                Detected Language:
              </MDBCardTitle>
              <MDBCardTitle style={{ marginTop: "5vh" }}>
              {detectedLanguage}
              </MDBCardTitle>
            </MDBCardBody>
          </MDBCard>
        </div>
      </div>
    </div>
  );
}

export default App;
